html:
{% macro render_field(df, doc) -%}
	{%- if df.fieldtype=="Table" -%}
		{{ render_table(df, doc) }}
	{%- elif df.fieldtype=="HTML" -%}
		<div>{{ frappe.render_template(df.options, {"doc": doc}) or "" }}</div>
	{%- elif df.fieldtype in ("Text", "Text Editor", "Code") -%}
		{{ render_text_field(df, doc) }}
	{%- else -%}
		{{ render_field_with_label(df, doc) }}
	{%- endif -%}
{%- endmacro -%}

{%- macro render_table(df, doc) -%}
	{%- set table_meta = frappe.get_meta(df.options) -%}
	{%- set data = doc.get(df.fieldname)[df.start:df.end] -%}
	{%- if doc.print_templates and
			doc.print_templates.get(df.fieldname) -%}
		{% include doc.print_templates[df.fieldname] %}
	{%- else -%}
		{%- if data -%}
		{%- set visible_columns = get_visible_columns(doc.get(df.fieldname),
			table_meta, df) -%}
		<div {{ fieldmeta(df) }}>
			<table class="table table-bordered table-condensed">
				<thead>
					<tr>
						<th style="width: 40px" class="table-sr">Sr</th>
						{% for tdf in visible_columns %}
							<th style="width: {{ get_width(tdf) }}px;" class="{{ get_align_class(tdf.fieldtype) }}" {{ fieldmeta(df) }}>
								/SINV%2F1718%2F193{{ _(tdf.label) }}</th>
						{% endfor %}
					</tr>
				</thead>
				<tbody>
					{% for d in data %}
					<tr>
						<td class="table-sr">{{ d.idx }}</td>
						{% for tdf in visible_columns %}
							<td class="{{ get_align_class(tdf.fieldtype) }}" {{ fieldmeta(df) }}>
                                <div class="value">{{ print_value(tdf, d, table_meta, doc) }}</div></td>
						{% endfor %}
					</tr>
					{% endfor %}
				</tbody>
			</table>
		</div>
		{%- endif -%}
	{%- endif -%}
{%- endmacro -%}

{% macro fieldmeta(df) -%}
data-fieldname="{{ df.fieldname }}" data-fieldtype="{{ df.fieldtype }}"
{%- endmacro %}

{%- macro render_field_with_label(df, doc) -%}
		<div class="row {% if df.bold %}important{% endif %} data-field" {{ fieldmeta(df) }}>
			<div class="col-xs-5 text-right">
				{% if df.fieldtype not in ("Image","HTML") and
					doc.get(df.fieldname) != None %}
				<label>{{ _(df.label) }}</label>
				{% endif %}
			</div>
			<div class="col-xs-7 {{ get_align_class(df.fieldtype) }} value">
				{% if doc.get(df.fieldname) != None -%}
					{{ print_value(df, doc) }}{% endif %}
			</div>
		</div>
{%- endmacro -%}

{%- macro render_text_field(df, doc) -%}
{%- if doc.get(df.fieldname) != None -%}
<div style="padding: 10px 0px" {{ fieldmeta(df) }}>
	{%- if df.fieldtype in ("Text", "Code") %}<label>{{ _(df.label) }}</label>{%- endif %}
	{%- if df.fieldtype=="Code" %}
		<pre class="value">{{ doc.get(df.fieldname) }}</pre>
	{% else -%}
		{{ doc.get_formatted(df.fieldname, parent_doc or doc) }}
	{% endif -%}
</div>
{%- endif -%}
{%- endmacro -%}

{%- macro print_value(df, doc, meta, parent_doc=None) -%}
    {% if doc.print_templates and
			doc.print_templates.get(df.fieldname) %}
        {% include doc.print_templates[df.fieldname] %}
	{% elif df.fieldtype=="Check" %}
		<i class="{{ 'icon-check' if doc[df.fieldname] else 'icon-check-empty' }}"></i>
	{% elif df.fieldtype=="Image" %}
		<img src="{{ doc[doc.meta.get_field(df.fieldname).options] }}" class="img-responsive">
	{% elif df.fieldtype in ("Attach", "Attach Image") and doc[df.fieldname]
		and (guess_mimetype(doc[df.fieldname])[0] or "").startswith("image/") %}
		<img src="{{ doc[df.fieldname] }}" class="img-responsive">
    {% elif df.fieldtype=="HTML" %}
        {{ frappe.render_template(df.options, {"doc":doc}) }}
	{% else %}
		{{ doc.get_formatted(df.fieldname, parent_doc or doc, translated=True) }}
	{% endif %}
{%- endmacro %}

{% macro get_width(df) -%}
	{%- if df.print_width -%}{{ (df.print_width|str).replace("px", "") }}
	{%- elif df.fieldtype in ("Int", "Check", "Float", "Currency") -%}{{ 80 }}
	{%- else -%}{{ 150 }}{% endif -%}
{%- endmacro %}

{% macro get_align_class(fieldtype) %}
	{%- if fieldtype in ("Int", "Float", "Currency") -%}{{ "text-right" }}
	{%- else -%}{{ "" }}
	{%- endif -%}
{% endmacro %}

{%- macro add_header(page_num, max_pages, doc, letter_head, no_letterhead, footer) -%}

	{% if letter_head and not no_letterhead %}
		<div class="col-xs-3 letter-head" style="float:left !important">{{ letter_head }}</div>
   		 <div class="col-xs-5 text-left"><br /><br />Supplier:<b> <br /> {{ doc.supplier_name }} </b><br /> {{ doc.address_display or "" }}</div>
 		<div class="col-xs-4">
			<br /><br />
			<div class="row text-right"># <b>{{ doc.name }}</b></div>
			<div class="row text-right">Shipping Date: <b>{{ doc.get_formatted("posting_date") }}</b></div>
		</div>
	{% endif %}

    {% if max_pages > 1 %}
    <p class="text-right">{{ _("Page #{0} of {1}").format(page_num, max_pages) }}</p>
    {% endif %}

{%- endmacro -%}

{% for page in layout %}
<div class="page-break">
	<div {% if print_settings.repeat_header_footer %} id="header-html" class="hidden-pdf" {% endif %}>

{{ add_header(loop.index, layout|len, doc, letter_head, no_letterhead, footer) }}
	</div>

	{% if print_settings.repeat_header_footer %}
	<div id="footer-html" class="visible-pdf">
		{% if not no_letterhead and footer %}
		<div class="letter-head-footer">
			{{ footer }}
		</div>
		{% endif %}
		<p class="text-center small page-number visible-pdf">
			{{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }}
		</p>
	</div>
	{% endif %}

<small>
<div class="row"><br /></div>
<div class="row"><br /></div>
<div class="row"></div>
<div class="row"></div>
<table class="table table-condensed table-hover table-bordered">
  <tbody>
        <tr>
   	    <th class="text-center">Pallets</th>
            <th class="text-center">Boxes</th>
            <th class="text-center">Product</th>
            <th class="text-center">Boxes/Pallet</th>
            <th class="text-center">lbs/box</th>
            <th class="text-center">Net Weight(lbs)</th>
            <th class="text-center">Gross Weight(lbs)</th>
        </tr>
        {%- for row in doc.items -%}
			{% if row.qty >= 0 %}
				<tr>
					<td style="width: 8%; text-align: center;">
						{% if row.boxes_pallet_for_purchase > 0 %}
							{% set plt = namespace(a = row.received_box|int / row.boxes_pallet_for_purchase|int) %}
							{{ plt.a|round }}
						{% else %}
							0
						{% endif %}
					</td>
					<td style="width: 8%; text-align: center;"><b>{{ row.received_box or '' }} </b> </td>
					<td style="width: 30%; text-align: left;">{{ row.item_name or ''}}</td>
					<td style="width: 7%; text-align: center;">{{row.boxes_pallet_for_purchase}}</td>
				   <td style="width:7%; text-align: center;">{{
						row.weight_per_unit or ''}}</td>
				   <td style="width: 18%; text-align: center;">{{
						row.weight_per_unit|int * row.received_box|int }}</td>
				   <td style="width: 22%; text-align: center;">{{
						row.gross_weight_lbs|int * row.received_box|int }}</td>
				</tr>
			{% endif %}
        {%- endfor -%}

	<tr>
            <th class="text-center">{{ doc.total_pallet|int }}</th>
            <th class="text-center">{{ doc.total_boxes|int }}</th>
            <th class="text-center"></th>
            <th class="text-left"></th>
            <th class="text-left"></th>
            <th class="text-center">{{ doc.total_net_weight }}</th>
            <th class="text-center">{{ doc.total_gross_weight_lbs }}</th>
        </tr>
    </tbody>
</table>
<br>



</small>





</div>
{% endfor %}
